INSERT INTO `ghs_gigpress_shows` VALUES(3, 2, 3, 2, '2014-12-13', 0, '19:30:00', '2014-12-13', '', '', '', '', 'To celebrate our 60th Anniversary, our Christmas program features music from our very own tradition. From the early years of the American republic, and from a wide range of early tune books and manuscripts, a generous selection of carols, New England anthems, Southern folk hymns and religious ballads for the season.', 0, 'active', '', 0, NULL, NULL, NULL, NULL, NULL, NULL);
