INSERT INTO `ghs_gigpress_shows` VALUES(11, 5, 9, 1, '2015-03-06', 0, '20:00:00', '2015-03-06', '', 'http://www.vendini.com/ticket-software.html?t=tix&amp;e=3464f077eac24824607a75183e523a3e', '', '', 'The constantly evolving and inventive musical minds of Italian and French masters during the fourteenth century has left us with repertoires, both sacred and secular, that successfully unite the search for new and different creative paths with astonishing lyricism and sensual beauty. In this specially commissioned program for the Massachusetts Institute of Technology, you will hear music spanning the worlds of God and Man, by the greatest composers of their day: Machaut, Landini, da Bologna, and others, performed by Camerata''s virtuoso soloists and instrumentalists.', 0, 'active', '', 0, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `ghs_gigpress_shows` VALUES(12, 6, 10, 2, '2015-03-28', 0, '16:00:00', '2015-03-28', '', 'http://portlandovations.org/shows/boston-camerata.shtml', '', '', 'Back by popular demand! An interfaith celebration unlike any other. Discover with us the common musical roots of Judaism, Islam, and Christianity, and the astonishing and beautiful interactions among these traditions. Our program includes elements of Jewish liturgy, Gregorian and Koranic chant, songs and texts of Jewish minstrels, Sephardic folksong, medieval Spanish Cantigas, and Judaeo-Islamic music from the ancient Andalusian tradition. The Boston Camerata is joined by Sharq Arabic Music Ensemble.\r\nConcert runtime 1 hour 45 min', 0, 'active', '', 0, NULL, NULL, NULL, NULL, NULL, NULL);
