INSERT INTO `ghs_gigpress_shows` VALUES(19, 4, 16, 2, '2014-12-05', 0, '20:30:00', '2014-12-05', '', '', '', '', 'Camerata’s chamber music version of this, our best-known holiday program, features three angelic women’s voices and instrumentalists. Our performance presents magnificent medieval song and poetry from the mid-winter celebration of Christ’s nativity, with a look as well at the pagan celebrations of winter solstice. Countries represented include France, England, Italy, Provence, and Germany.​ This production will include also a distaff choir from Mission Voix Alsace. ​', 0, 'active', '', 0, NULL, NULL, NULL, NULL, NULL, NULL);
