INSERT INTO `ghs_gigpress_shows` VALUES(25, 11, 5, 1, '2015-12-18', 0, '20:00:00', '2015-12-18', '', 'http://bostoncamerata.org/tickets.html', '', '', 'The Christmas narrative retold using songs, chants, and instrumental pieces from the countries of the Mediterranean basin: Spain, Italy, and southern France, as well as north Africa and the Holy Land.  Works are drawn from medieval manuscripts and more recent, though still archaic, folklore and oral traditions. With voices, early instruments of Europe and the Middle East, and readings of the Christmas story. Camerata is joined by SHARQ Arabic Ensemble.\r\n\r\nAnne Azéma - voice, hurdy-gurdy &amp; director\r\nCamila Parias - voice\r\nDeborah Rentz-Moore - voice\r\nSteven Lundahl - brass, recorder\r\nKarim Nagi - percussion, voice\r\nBoujemaa Ragui - oud, ney, voice, percussion\r\nMehmet Sanlikol - voice, oud\r\nBeth Bahia Cohen - vielle', 2657, 'active', '', 0, NULL, NULL, NULL, NULL, NULL, NULL);
