INSERT INTO `ghs_gigpress_shows` VALUES(42, 18, 8, 2, '2016-09-10', 0, '20:30:00', '2016-09-10', '', '', '', '', 'Boston Camerata presents Roman de Fauvel for "Festival Voix et Route Romane". Jean Lorrain, narrator, voice; Anne Azéma, voice, hurdy-gurdy (Fortune); Tim Evans, voice (Fauvel); Joel Cohen, lauta, voice; Shira Kammen, vielle, harp, voice', 0, 'active', 'http://www.voix-romane.com/', 0, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `ghs_gigpress_shows` VALUES(43, 16, 4, 2, '2016-12-18', 0, '15:00:00', '2016-12-18', '$30', 'http://unioncollegeconcerts.org/collections/concerts/products/boston-camerata-12-18-16', '', '', 'In the European North, the forests are deep; the nights are dark and long. Perhaps this is why, in reaction, the early Christmas music of the German-speaking peoples is so intensely joyful, so profoundly rich. Our program explores the marvelous music of German Christmas festivity through chants and chorales, simple carols, grandiose polyphony, and instrumental fantasias of the 15th to early 17th centuries. This new program will feature the stellar Boston Camerata Wind Ensemble and an expanded consort of voices and early instruments. \r\nAnne Azéma, Daniel Hershey, Camila Parias, Deborah Rentz-Moore, John Taylor Ward, voices; Brian Kay, Steven Lundahl, Liza Malamut, sackbuts;   Nathaniel Cox, cornetto, theorbo; Carol Lewis, viola da gamba.', 0, 'active', '', 0, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `ghs_gigpress_shows` VALUES(44, 1, 28, 2, '2017-01-26', 0, '17:00:00', '2017-01-26', '', '', '', '', 'This powerful, highly-praised production returns to Boston in 2017 as part of a national tour. The themes of justice, and of truth spoken to power, are once again front and center as the Jewish captive Daniel confronts the tyrannical Belshazzar. The magnificent musical play of Daniel, composed eight centuries ago in Beauvais, France was newly transcribed from the original manuscript source  and powerfully staged for modern audiences by Anne Azéma, it was premiered in Boston in 2014 to critical and public acclaim.', 0, 'active', '', 0, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `ghs_gigpress_shows` VALUES(45, 1, 29, 2, '2017-01-31', 0, '20:00:00', '2017-01-31', '', 'http://www.cathedralconcerts.org/product-category/concert-tickets/', '', '', 'This powerful, highly-praised production returns to Boston in 2017 as part of a national tour. The themes of justice, and of truth spoken to power, are once again front and center as the Jewish captive Daniel confronts the tyrannical Belshazzar. The magnificent musical play of Daniel, composed eight centuries ago in Beauvais, France was newly transcribed from the original manuscript source and powerfully staged for modern audiences by Anne Azéma, it was premiered in Boston in 2014 to critical and public acclaim. Presented in collaboration with Trinity Church; with the Trinity Choristers, Boston City Singers, and Longy School of Music of Bard College.', 0, 'active', '', 0, NULL, NULL, NULL, NULL, NULL, NULL);
