INSERT INTO `ghs_gigpress_shows` VALUES(51, 4, 34, 2, '2016-12-09', 0, '19:30:00', '2016-12-09', '', 'http://www.sdems.org/', '', '', 'A glimpse of Christmas spirituality from Medieval France, Italy, England, and Provence, including music of the church and songs of private devotion around the joyous theme of the Nativity. Included are songs to the Virgin Mary, processionals from Saint Martial of Limoges, hymns, lyrics, and miracle ballads sung in Latin, Old French, Old Provençal, and Saxon, interlaced with Medieval English texts of the Nativity. Our cast features an extraordinary trio of women’s voices with harp and vielle.\r\n\r\nPresented by San Diego Early Music Society.', 0, 'active', '', 0, NULL, NULL, NULL, NULL, NULL, NULL);
