INSERT INTO `ghs_gigpress_shows` VALUES(57, 20, 37, 1, '2017-07-08', 0, '20:00:00', '2017-07-08', '', 'http://tickets.rockportmusic.org/single/SelectSeating.aspx?p=4361', '', '', '<img src="/blog/images/lnlssampler.jpg" class="alignleft" width="20%">Rockport Chamber Music Festival presents Camerata''s groundbreaking telling of the Tristan and Iseult legend. Based on the original 12th-century sources, this medieval romance with narration weaves original elements together with staging and light for a truly unique concert experience.\r\n<p>\r\nAnne Azéma as Iseult<br>Sumner Thompson as Tristan<br>Clare McNamara as Brangane<br>Jason McStoots as Mark<br>Joel Cohen as the Narrator<br>with Susanne Ansorg, vielle<br> lighting design by Peter Torpey.', 0, 'active', 'http://rockportmusic.org/boston-camerata/', 0, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `ghs_gigpress_shows` VALUES(58, 21, 38, 2, '2017-07-29', 0, '14:00:00', '2017-07-29', '', 'www.bostoncamerata.org/tickets.html', '', '', '<img src="/blog/images/tristan.gif" class="alignleft" width="20%">We offer a summer buffet of music for our Maine friends, plus a sneak preview of our 2017-18 season! An opening set of love songs, chants, and spirituals from medieval France is followed by a feast of home-cooked ballads, Revolutionary partsongs, and paeans to American heroes, including pieces from the earliest Maine songbooks. Anne Azéma, director, voice; Michael Barrett, Daniel Hershey, Camila Parias, Deborah Rentz-Moore, Donald Wilkinson, voices; Joel Cohen, voice, guitar.', 0, 'active', '', 0, NULL, NULL, NULL, NULL, NULL, NULL);
